class Queue:
    def __init__(self):
        self.items = []
        self.front = None
        self.back = None

    # Enqueue an item to the back of the queue
    def enqueue(self, item):
        if self.back is None:
            self.back = item
        else:
            self.back = self.back + [item]

    # Dequeue an item from the back of the queue
    def dequeue(self):
        if self.back is not None:
            return self.back.pop()
        else:
            return None

    # Peek at the item at the front of the queue
    def peek(self):
        if self.front is not None:
            return self.front.pop()
        else:
            return None

def find_common_elements(arr1, arr2):
    # Traverse both the arrays and find out the elements present in both
    arr1_hash = {}
    for i in arr1:
	if i in arr1_hash:
	    arr1_hash[i] += 1
	else:
	    arr1_hash[i] = 1

    arr2_hash = {}
    for i in arr2:
	if i in arr2_hash:
	    arr2_hash[i] += 1
	else:
	    arr2_hash[i] = 1

    # Traverse both the arrays again and find the common elements
    common_elems = []
    for i in arr1_hash:
	if i in arr2_hash:
