function is_nearest_player(player)
{
    // Get the player's position
    var playerPos = player.getPosition();
    // Get the player's radius
    var playerRadius = player.getRadius();
    // Get the X, Y of the nearest player
    var nearestX = playerPos.x;
    var nearestY = playerPos.y;
    // Check if the player is closer to the right or left side
    if (playerRadius.x < playerRadius.y)
    {
        // If player is closer to the right, set the nearestX to the playerPos.x
        nearestX = playerPos.x;
    } 
    else
    {
        // If player is closer to the left, set the nearestX to the playerPos.y
        nearestX = playerPos.y;
    }
    // Check if the player is closer to the top or bottom
    if (playerRadius.y < playerRadius.x)
    {
        // If player is closer to the top, set the nearestY to the playerPos.y
        nearestY = playerPos.y;
    } 
    else
    {
        // If player is closer to the bottom, set the nearestY to the playerPos.x
        nearestY = playerPos.x;
    }
    // Return the nearest player
    return player;
}

