def is_prime(num):
    if num == 2:
        return True
    if num < 2 or num % 2 == 0:
        return False
    # Check if a number is divisible by other numbers
    i = 3
    while i * i <= num:
        if num % i == 0:
            return False
        i += 2
    return True

