def longest_common_subsequence(s1, s2): 
    """
    This function finds the longest common subsequence of two strings s1 and s2.

    Parameters:
    s1 {string}
    s2 {string}

    Returns:
    The longest common subsequence of s1 and s2.
    """

    if len(s1) == 0 or len(s2) == 0:
        return ""

    def get_score(a, b):
        # Compute the score for each substring
        score = 0
        for i in range(len(a)):
            for j in range(len(b)):
                if a[i] == b[j] and i > 0 and j > 0:
                    score += 1
                else:
                    score -= 1
        return score

    # Compute the score for each substring in the two strings
    s1_score = get_score(s1, s2)
    s2_score = get_score(s2, s1)

    # Determine the length of the longest common subsequence
    n = max(len(s1), len(s2))
    lcs = [0] * n
    for i in range(n):
        if i == 0:
            lcs[i] = s1_score
        else:
            lcs[i] = max(lcs[i - 1], s2_score)
    # Print the longest common subsequence
    print("The longest common subsequence of " + s1 + " and " + s2 + " is:")
    for i in range(n):
        print(lcs[i])
    return lcs[n - 1]

