void convertMarkdownToHtml(std::string& markdown, std::string& html)
{
    const std::string markdown_regex("(?<=^|[\\W_]+)(?=([^<>\s]+)(?<![^<>\s]+)");
    const std::string html_regex("(?<=^|[\\W_]+)(?=([^<>\s]+)(?<![^<>\s]+)");

    std::regex markdown_regex(markdown_regex);
    std::regex html_regex(html_regex);

    std::string result;
    std::size_t match_markdown = markdown.find(markdown_regex);
    std::size_t match_html = markdown.find(html_regex);

    if (match_markdown != std::string::npos)
    {
        result.assign(markdown.substr(0, match_markdown));
        result.replace(match_markdown, markdown_regex, "<html><head></head><body>");
    }
    else if (match_html != std::string::npos)
    {
        result.assign(markdown.substr(0, match_html));
        result.replace(match_html, html_regex, "<html><head></head><body>");
    }
    else
    {
        result = markdown;
    }

    html.assign(result);
}

