from collections import deque

class Queue:
    def __init__(self):
        self.left_stack = []
        self.right_stack = []

    def enqueue(self, item):
        self.left_stack.append(item)

    def dequeue(self):
        if not self.left_stack:
            return self.right_stack.pop()
        else:
            return self.left_stack.pop()

    def is_empty(self):
        return not self.left_stack

    def peek(self):
        if not self.left_stack:
            return self.right_stack.pop()
        else:
            return self.left_stack.pop()

    def __str__(self):
        return str(self.left_stack)
