#include <iostream>
#include <fstream>
#include <string>
#include <sstream>
#include <algorithm>
#include <cctype>

using namespace std;

// Function to convert a string to lowercase
string toLowerCase(const string& str) {
    string lowerStr = str;
    transform(lowerStr.begin(), lowerStr.end(), lowerStr.begin(), ::tolower);
    return lowerStr;
}

// Function to remove punctuation from a string
string removePunctuation(const string& word) {
    string cleanWord;
    for (char ch : word) {
        if (!ispunct(ch)) {
            cleanWord += ch;
        }
    }
    return cleanWord;
}

// Function to count occurrences of a word in a line
int countOccurrencesInLine(const string& line, const string& word) {
    istringstream iss(line);
    string currentWord;
    int count = 0;

    while (iss >> currentWord) {
        if (removePunctuation(toLowerCase(currentWord)) == word) {
            count++;
        }
    }
    return count;
}

int main() {
    string filename;
    string wordToCount;
    int totalCount = 0;

    // Get the filename and the word to count from the user
    cout << "Enter the filename: ";
    cin >> filename;
    cout << "Enter the word to count: ";
    cin >> wordToCount;

    // Convert the word to lowercase for case-insensitive comparison
    wordToCount = toLowerCase(wordToCount);

    ifstream file(filename);
    if (!file.is_open()) {
        cerr << "Unable to open file " << filename << endl;
        return 1;
    }

    string line;

    // Read the file line by line and count occurrences
    while (getline(file, line)) {
        totalCount += countOccurrencesInLine(line, wordToCount);
    }

    file.close();

    cout << "The word '" << wordToCount << "' occurred " << totalCount << " times in the file " << filename << "." << endl;

    return 0;
}
```

