class Solution {
    private static final int MAX_POWER_OF_THREE = 1162261467; // 3^19

    public boolean isPowerOfThree(int n) {
        return n > 0 && MAX_POWER_OF_THREE % n == 0;
    }

    public static void main(String[] args) {
