// Handle inline elements like bold, italic, code, and links
std::string convertInlineElements(const std::string& markdown) {
    std::string html = markdown;
    std::unordered_map<std::string, std::string> inline_replacements = {
        {"\\*\\*([^\\*]+)\\*\\*", "<strong>$1</strong>"}, // Bold
        {"\\*([^\\*]+)\\*", "<em>$1</em>"},              // Italic
        {"`([^`]+)`", "<code>$1</code>"},                // Inline code
        {"\\[([^\\]]+)\\]\\(([^\\)]+)\\)", "<a href=\"$2\">$1</a>"} // Link
    };

    for (const auto& [pattern, replacement] : inline_replacements) {
        html = std::regex_replace(html, std::regex(pattern), replacement);
    }

    return html;
}

// Handle block elements like headers, code blocks, and lists
std::string convertBlockElements(const std::string& markdown) {
    std::string html = markdown;
    std::vector<std::pair<std::string, std::string>> block_replacements = {
        {"^(#{1,6}) (.*)$", "<h$1>$2</h$1>\n"}, // Headings
        {"^```(.*)\n((.|\\n)*?)```", "<pre><code>$2</code></pre>\n"}, // Code block
        {"^\\* (.*)$", "<li>$1</li>\n"}, // Unordered list
        {"^\\d+\\. (.*)$", "<li>$1</li>\n"} // Ordered list
    };

    for (const auto& [pattern, replacement] : block_replacements) {
        html = std::regex_replace(html, std::regex(pattern, std::regex::multiline), replacement);
    }

    return html;
}

// Handle paragraphs
std::string convertParagraphs(const std::string& markdown) {
    std::istringstream iss(markdown);
    std::ostringstream oss;
    std::string line;
    bool inParagraph = false;

    while (std::getline(iss, line)) {
        if (line.empty()) {
            if (inParagraph) {
                oss << "</p>\n";
                inParagraph = false;
            }
        } else if (line.substr(0, 2) != "<h" && line.substr(0, 3) != "<li" && line.substr(0, 5) != "<pre>") {
            if (!inParagraph) {
                oss << "<p>";
                inParagraph = true;
            }
            oss << line << " ";
        } else {
            if (inParagraph) {
                oss << "</p>\n";
                inParagraph = false;
            }
            oss << line << "\n";
        }
    }

    if (inParagraph) {
        oss << "</p>\n";
    }

    return oss.str();
}

// Convert markdown to HTML
std::string markdownToHtml(const std::string& markdown) {
    std::string html = markdown;
    html = convertBlockElements(html);
    html = convertInlineElements(html);
    html = convertParagraphs(html);
    return html;
}

