    if (i == 0) {
      // Draw head
      drawImage(snakeHeadImg, snake[i].x, snake[i].y); 
    } else {
      // Draw body  
      drawImage(snakeBodyImg, snake[i].x, snake[i].y);
    }
  }
}

// Game loop
while(!gameOver) {

  // Clear screen
  clearDevice(); 

  // Draw images
  drawImage(foodImg, foodX, foodY);
  drawSnake(snake, snakeLength);

  // Update positions
  updateSnakePosition();

  // Add delay for smooth animation
  delay(100);

}

// Function for smooth movement
void updateSnakePosition() {

  // Update next head position
  snake[0].x += xVelocity;
  snake[0].y += yVelocity;

  // Move rest of segments
  for(int i=1; i<snakeLength; i++) {
