#include <string>
#include <regex>

std::string markdownToHTML(const std::string& markdown) {
    std::string html;
    std::regex headingRegex(R"(^(#{1,6})\s*(.+?)\s*#*$)");
    std::regex emphasisRegex(R"((\*\*|__)(.*?)\1)");
    std::regex linkRegex(R"(\[(.*?)\]\((.*?)\))");
    std::regex codeBlockRegex(R"(^```(.*?)\n([\s\S]*?)\n```$)");
    std::regex inlineCodeRegex(R"(`(.*?)`$)");

    std::sregex_iterator iter(markdown.begin(), markdown.end(), headingRegex);
    std::sregex_iterator end;

    for (std::sregex_iterator i = iter; i != end; ++i) {
        std::smatch match = *i;
        int level = match[1].str().length();
        std::string text = match[2].str();
        html += "<h" + std::to_string(level) + ">" + text + "</h" + std::to_string(level) + ">";
    }

    html = std::regex_replace(html, emphasisRegex, "<em>$2</em>");
    html = std::regex_replace(html, linkRegex, "<a href=\"$2\">$1</a>");

    iter = std::sregex_iterator(markdown.begin(), markdown.end(), codeBlockRegex);
    for (std::sregex_iterator i = iter; i != end; ++i) {
        std::smatch match = *i;
        std::string lang = match[1].str();
        std::string code = match[2].str();
        html += "<pre><code" + (lang.empty() ? "" : " class=\"language-" + lang + "\"") + ">" + code + "</code></pre>";
    }

    html = std::regex_replace(html, inlineCodeRegex, "<code>$1</code>");

    return html;
}
```

