    std::string wordToCount("algorithm"); // Change this to the word you want to count
    std::ifstream inputFile("input.txt"); // Change this to the path of your input file

    if (!inputFile.is_open()) {
        std::cerr << "Failed to open the input file." << std::endl;
        return 1;
    }

    int wordCount = 0;

    std::string line;
    while (std::getline(inputFile, line)) {
        size_t position = line.find(wordToCount);
        if (position != std::string::npos) {
            wordCount++;
        }
    }

    inputFile.close();

    if (wordCount > 0) {
