{
    const string word = "apple";
    ifstream file ("input.txt");
    int count = 0;

    while (file >> line)
    {
        count += count(line, word);
    }

    file.close();

    cout << "Occurrences of " << word << ": " << count << endl;
    return 0;
}

int count(string line, const string &word)
{
    int count = 0;
    string tokens[] = line.split(" ");
