        html += markdown_content.substr(start_pos, end_pos - start_pos + 2) + "<br>";
        start_pos = end_pos + 2;
    }

    html += markdown_content.substr(start_pos);

    std::stringstream out;
    out << "<p>" << html << "</p>";
    out << "</body>" << std::endl;
    out << "</html>";

