std::string markdownToHtml(const std::string& markdown) {
    std::string html;
    std::unordered_map<std::string, std::string> markdownToHtmlMap = {
        {"# ", "<h1>"},
        {"## ", "<h2>"},
        {"*** ", "<strong>"},
        {"* ", "<em>"},
        {"\n", "<br/>"},
        // Add more mappings for other markdown elements here (lists, links, etc.)
    };

    for (size_t i = 0; i < markdown.size(); ++i) {
        for (const auto& [markdownTag, htmlTag] : markdownToHtmlMap) {
            if (markdown.substr(i, markdownTag.length()) == markdownTag) {
                html += htmlTag;
                i += markdownTag.length() - 1; // Adjust index to avoid processing the same tag again
                break; // Move to the next character after replacing
            }
        }
        html += markdown[i]; // Append regular characters directly
    }

    html += "</h3>"; // Close any opened tags (assuming basic hierarchy)

    return html;
}

int main() {
    std::string markdown = "# Hello\n## World\n***This is bold***\n*This is italic*";
    std::string html = markdownToHtml(markdown);
    std::cout << html << std::endl;
    return 0;
}
```

