function findPrimeInArray(numbers) {
  if (numbers.length !== 5) {
    console.error("Input array must contain exactly 5 numbers.");
    return;
  }

  for (const num of numbers) {
    if (isPrime(num)) {
      console.log(`Prime number found: ${num}`);
      return; // Exit after finding one prime
    }
  }

  console.log("No prime numbers found in the array.");
}

function isPrime(num) {
  // Handle cases less than 2
  if (num <= 1) return false;
  if (num <= 3) return true; 

  // Check divisibility by 2 and 3
  if (num % 2 === 0 || num % 3 === 0) return false;

  // Optimized primality test: Check divisors up to the square root
  for (let i = 5; i * i <= num; i += 6) {
    if (num % i === 0 || num % (i + 2) === 0) return false;
  }
  return true;
}

// Example usage:
