using namespace std;

int main()
{
  // Open the text file
  ifstream file("text.txt");

  // Create a variable to store the word to search for
  string wordToSearch = "the";

  // Create a variable to store the number of occurrences
  int numOccurrences = 0;

  // Read the file line by line
  string line;
  while (getline(file, line))
  {
    // Search for the word in the line
    string lineLower = line.ToLower();
    size_t pos = lineLower.find(wordToSearch);

    // If the word is found, increment the number of occurrences
    if (pos != string::npos)
    {
      numOccurrences++;
    }
  }

  // Close the file
  file.close();

  // Print the number of occurrences
  cout << "The word '" << wordToSearch << "' appears " << numOccurrences << " times in the text file." << endl;

  return 0;
}
```

