std::string markdownToHTML(const std::string& markdownText) {
    // Regular expressions to match markdown syntax and convert to HTML
    std::regex italicRegex("\\*(.*?)\\*");
    std::regex boldRegex("\\*\\*(.*?)\\*\\*");
    std::regex headerRegex("# (.*)");

    // Replace markdown syntax with HTML tags
    std::string htmlText = std::regex_replace(markdownText, italicRegex, "<em>$1</em>");
    htmlText = std::regex_replace(htmlText, boldRegex, "<strong>$1</strong>");
    htmlText = std::regex_replace(htmlText, headerRegex, "<h1>$1</h1>");

