function checkForPrimeNumbers(arr) {
  for (let i = 0; i < arr.length; i++) {
    let num = arr[i];
    let isPrime = true;

    if (num <= 1) {
      isPrime = false;
    } else if (num === 2) {
      isPrime = true;
    } else if (num % 2 === 0) {
      isPrime = false;
    } else {
      for (let j = 3; j <= Math.sqrt(num); j += 2) {
        if (num % j === 0) {
          isPrime = false;
          break;
        }
      }
    }

    if (isPrime) {
