const { Client, Intents } = require('discord.js');

// Create a new client instance
const client = new Client({ intents: [Intents.FLAGS.GUILDS] });

// When the bot is ready, log "Ready!" to the console
client.on('ready', () => {
  console.log('Ready!');
});

// Create a slash command for the ban command
client.on('interactionCreate', async interaction => {
  if (!interaction.isCommand()) return;

  if (interaction.commandName === 'ban') {
    // Check if the user has the necessary permission to ban members
    if (!interaction.member.permissions.has('BAN_MEMBERS')) {
      // If not, send an error message
      await interaction.reply({ content: 'You do not have permission to use this command.', ephemeral: true });
      return;
    }

    // Get the user to ban from the slash command options
    const userToBan = interaction.options.getUser('user');

    // Check if the user is valid
    if (!userToBan) {
      // If not, send an error message
      await interaction.reply({ content: 'Please provide a valid user to ban.', ephemeral: true });
      return;
    }

    // Ban the user with a reason, if provided
    const reason = interaction.options.getString('reason') || 'No reason provided.';
    await interaction.guild.members.ban(userToBan, { reason: reason });

    // Send a success message
    await interaction.reply({ content: `${userToBan.tag} has been banned for: ${reason}`, ephemeral: true });
  }
});

