public class Solution {
    public boolean isPowerOfThree(int n) {
        // Edge cases
        if(n == 0)
            return false;
        if(n == 1)
            return true;

        // Check if n is a multiple of 3
        while(n%3 == 0){
            n = n/3;
        }

        // If n is equal to 1 at this point, it is a power of 3
        return n == 1;
    }
}

