// Function to calculate the nth Fibonacci number using dynamic programming
int findFibonacci(int n) {
    // Create an array to store the calculated Fibonacci numbers
    int fib[n+1];
    // Initialize the first two Fibonacci numbers as 0 and 1
    fib[0] = 0;
    fib[1] = 1;
    // Loop through the remaining numbers
    for (int i = 2; i <= n; i++) {
        // Calculate the Fibonacci number and store it in the array
        fib[i] = fib[i-1] + fib[i-2];
    }
    // Return the nth Fibonacci number
    return fib[n];
}

