class Queue:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def enqueue(self, item):
        self.stack1.append(item)

    def dequeue(self):
        if not self.stack2:
            while self.stack1:
                self.stack2.append(self.stack1.pop())
        if self.stack2:
            return self.stack2.pop()
        else:
            print("Queue is empty.")

    def size(self):
        return len(self.stack1) + len(self.stack2)

    def isEmpty(self):
        if self.size() == 0:
            return True
        else:
            return False

    def peek(self):
        if not self.stack2:
            while self.stack1:
                self.stack2.append(self.stack1.pop())
        if self.stack2:
            return self.stack2[-1]
        else:
            print("Queue is empty.")

    def display(self):
        if not self.stack2:
            while self.stack1:
                self.stack2.append(self.stack1.pop())
        print(self.stack2)

