#include <iostream>
#include <string>
#include <sstream>
#include <regex>

std::string markdownToHtml(const std::string& markdown) {
    std::string result;
    std::istringstream iss(markdown);
    std::string line;

    while (std::getline(iss, line)) {
        line = std::regex_replace(line, std::regex("^### (.*)"), "<h3>$1</h3>");
        line = std::regex_replace(line, std::regex("^## (.*)"), "<h2>$1</h2>");
        line = std::regex_replace(line, std::regex("^# (.*)"), "<h1>$1</h1>");
        line = std::regex_replace(line, std::regex("\\*\\*(.*)\\*\\*"), "<strong>$1</strong>");
        line = std::regex_replace(line, std::regex("\\*(.*)\\*"), "<em>$1</em>");
        line = std::regex_replace(line, std::regex("\\[(.*)\\]\\((.*)\\)"), "<a href=\"$2\">$1</a>");

        result += line + "\n";
    }

    return result;
}

int main() {
    std::string markdown = "# Header 1\n## Header 2\n### Header 3\n**Bold Text**\n*Italic Text*\n[Link Example](https://www.example.com)";
    std::string html = markdownToHtml(markdown);
    std::cout << "HTML:\n" << html << std::endl;

    return 0;
}
```

