        return 1;
    }

    std::string line;
    int totalCount = 0;

    while (std::getline(inputFile, line)) {
        totalCount += countWordOccurrences(line, wordToCount);
    }

    inputFile.close();

    std::cout << "The word \"" << wordToCount << "\" appears " << totalCount << " times in the file." << std::endl;

    return 0;
}
