#include <string>
#include <regex>

std::string markdownToHtml(std::string& markdown) {
    std::string html = markdown;

    // Convert headers
    for(int i = 6; i > 0; --i) {
        std::string mdHeader(i, '#');
        std::string htmlHeader = "<h" + std::to_string(i) + ">$1</h" + std::to_string(i) + ">";
        std::regex headerRegex("(^|\n)" + mdHeader + " (.*)");
        html = std::regex_replace(html, headerRegex, "$1" + htmlHeader);
    }

    // Convert bold
    std::regex boldRegex("\\*\\*(.*?)\\*\\*");
    html = std::regex_replace(html, boldRegex, "<b>$1</b>");

    // Convert italics
    std::regex italicsRegex("\\*(.*?)\\*");
    html = std::regex_replace(html, italicsRegex, "<i>$1</i>");

    // Convert links
    std::regex linkRegex("\\[(.*?)\\]\\((.*?)\\)");
    html = std::regex_replace(html, linkRegex, "<a href='$2'>$1</a>");

    return html;
}
```

