        const targetUser = interaction.options.getUser('target');

        if (!targetUser) {
            return await interaction.reply(`Please mention the user you want to ban.`);
        }

        const member = interaction.guild.members.resolve(targetUser);

        if (member) {
            member.ban()
                .then(() => {
                    interaction.reply(`Banned ${targetUser.tag}`);
                })
                .catch(err => {
                    interaction.reply('I was unable to ban the member');
                    console.error(err);
                });
        } else {
            interaction.reply("That user isn't in this guild!");
        }
    }
});

// Login to Discord with your app's token
client.login('your-token-goes-here');
```

