    if (!file.is_open()) {
        std::cerr << "Error opening the file." << std::endl;
        return 1;
    }

    // Read the file line by line
    while (file >> currentWord) {
        // Increment the count if the current word matches the search word
        if (currentWord == searchWord) {
            ++wordCount;
        }
    }

    // Close the file
    file.close();

    // Output the result
    std::cout << "The word '" << searchWord << "' occurs " << wordCount << " times in the file." << std::endl;

    return 0;
}
