#include <boost/markdown.hpp>
#include <string>

void convert_markdown_to_html(const std::string& markdown_string, std::string& html_string) {
    boost::markdown::html_writer::ptr writer = boost::markdown::html_writer::create();
    boost::markdown::html_document d;
    d.push_back(writer);

    boost::markdown::parser p;
    p.push_back(d);
    p.run(markdown_string);

    html_string = p.root().to_html();
}
