public class ArrayXor {
    public static void main(String[] args) {
        int[] arr1 = {1, 2, 3, 4};
        int[] arr2 = {2, 3, 4, 5};

        int[] result = xor(arr1, arr2);
        System.out.println(Arrays.toString(result)); // Output: [false, true, false, false]
    }

    public static int[] xor(int[] arr1, int[] arr2) {
        int[] result = new int[arr1.length];

        for (int i = 0; i < arr1.length; i++) {
            result[i] = arr1[i] ^ arr2[i];
        }

        return result;
    }
}
