const { defineSupportCode } = require("cucumber");
const { AfterAll, BeforeAll, BeforeFeature, AfterFeature } = require("@cypress/cucumber-preprocessor");

// Plugins
const { CypressPlugin } = require("cypress-cucumber-preprocessor");

// Custom commands
const screenshots = require("cypress-screenshot-custom-command");

// Global variables
let currentScenario;

// Plugin installation
module.exports = (on, config) => {
  on("before:run", () => {
    return Cypress.run({
      spec: "cypress/integration/**/*.spec.js",
      config: {
        defaultCommandTimeOut: 60000,
        screenshotOnRunFailure: true,
        video: false,
        chromiumWebSecurity: false,
        experimentalFetchPolyfill: true,
        env: {
          TEST_ENV: "docker",
        },
      },
    }).then((results) => {
      results.stats.totalFailed += results.stats.total Skipped;
      if (results.stats.totalFailed > 0) {
        throw new Error(`Tests failed:\n${results.logs.slice(0, -1).join("\n")}`);
      }
    });
  });

  on("after:run", (_context, runnable) => {
    const errors = runnable.failed || [];
    if (errors.length) {
      errors.forEach((error) => {
        console.error(error.message);
        const screenshotPath = `${error.screenshotPath}`;
        if (screenshotPath) {
          console.log("Screenshot taken:", screenshotPath);
        }
      });
    }
  });

  config.addPlugin(CypressPlugin, {
    defaultCommands: {
      screenshot: screenshots,
    },
  });

  defineSupportCode(function ({ AfterAll, BeforeAll, BeforeFeature, AfterFeature }) {
    BeforeAll(function () {
      // Your code here
    });

    BeforeFeature(function (feature) {
      currentScenario = feature.keyword + " - " + feature.name;
    });

    AfterFeature(function () {
      // Your code here
    });
  });
};

```

