#include <iostream>
#include <fstream>
#include <string>

int count_word_occurrences(std::ifstream& file, const std::string& word) {
    int count = 0;
    std::string line;
    while (std::getline(file, line)) {
        std::size_t pos = 0;
        while ((pos = line.find(word, pos)) != std::string::npos) {
            count++;
            pos += word.length();
        }
    }
    return count;
}

int main() {
    std::string file_name = "example.txt";
    std::string word = "example";

    std::ifstream file(file_name);
    if (!file.is_open()) {
        std::cout << "Unable to open file!" << std::endl;
        return 1;
    }

    int occurrences = count_word_occurrences(file, word);
    std::cout << "Word '" << word << "' occurrences: " << occurrences << std::endl;

    file.close();
    return 0;
}

