#include <iostream>
#include <string>

#include <boost/markdown.hpp>

std::string convert_markdown_to_html(const std::string& markdown) {
    std::string html;
    boost::markdown::markdown_parser::md_result result;

    result = boost::markdown::markdown_parser::parse_markdown(markdown);

    if (result.suffix_type == boost::markdown::not_a_suffix) {
        for (auto& p : result) {
            html += p.second;
        }
    } else {
        std::string error_message = "Invalid markdown input: " + result.error_message;
        throw std::runtime_error(error_message);
    }

    return html;
}

int main() {
    std::string markdown = "# Heading\nThis is a *bold* heading.";
    std::string html = convert_markdown_to_html(markdown);

    std::cout << html << std::endl;

    return 0;
}
