class Queue:
    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def is_empty(self):
        return not self.stack1 and not self.stack2

    def enqueue(self, item):
        self.stack1.append(item)

    def dequeue(self):
        if not self.stack1:
            item = self.stack2.pop()
            if not self.stack2:
                self.stack1.append(item)
        else:
            return self.stack1.pop()

    def peek(self):
        if not self.stack1:
            return self.stack2.pop()
        return self.stack1[-1]

