    def dequeue(self):
        if not self.is_empty():
            if self.stack1:
                return self.stack1.pop()
            else:
                while self.stack2:
                    self.stack1.append(self.stack2.pop())
                return self.stack1.pop()

    def peek(self):
        if not self.is_empty():
            return self.stack1[-1]
