    std::string html = "";

    // Regular expression pattern to match markdown text
    std::regex pattern("(^[*#]+)(.*)([*#]+$)");

    // Loop through each match found by the regular expression pattern
    for (std::sregex_iterator it(markdown.begin(), markdown.end(), pattern);
         it!= std::sregex_iterator(); it++) {

