import std.stdio;

struct Item {
    let data: i32;
    let comparer: @compare.Ordering<Item>;
};

impl Item {
    fn new(data: i32) -> Self {
        Self { data, comparer: compare.default() }
    }
}

impl <T: Ord> @compare.Ordering<T> {
    fn lt(&self, other: &Self) -> bool {
        *self.data < *other.data
    }

    fn eq(&self, other: &Self) -> bool {
        *self.data == *other.data
    }

    fn gt(&self, other: &Self) -> bool {
        *self.data > *other.data
    }
}

impl <T: Ord> PartialOrd for Item<T> {
    fn partial_cmp(&self, other: &Self) -> Option<cmp::Ordering> {
        (self.lt)(other), (self.eq)(other), (self.gt)(other)
    }
}

impl <T: Ord> Ord for Item<T> {
    fn cmp(&self, other: &Self) -> cmp::Ordering {
        (**self.partial_cmp)(other).unwrap()
    }
}

impl <T: Ord> Debug for Item<T> {
    fn fmt(&self, f: &mut std.fmt.Formatter<'_>) -> std.fmt.Result {
        write!(f, "Item({:08b})", self.data)
    }
