public class ThreePower {
    public static boolean isThreePower(int n) {
        // Base case: n is less than 0 and negative
        if (n < 0) return false;

        // Break early if the number is not a power of three
        if (n < 3) return false;

        // Convert n to binary and create a bitset for the power of three
        BitSet powerThree = new BitSet(32);
        powerThree.set(0);
        powerThree.set(1);
        powerThree.set(2);

        // Bitwise and the binary representation of n with the power of three
        int mask = powerThree.cardinality();
        return (n & mask) == mask;
    }
}
