std::string markdownToHtml(std::string const& markdown) {
    std::string html;
    std::istringstream iss(markdown);
    std::ostringstream oss;

    std::unordered_map<std::string, int> md_to_html = {
        {"### ", 3},
        {"##", 3},
        {"### ", 3},
        {"##", 3},
        {"### ", 3},
        {"##", 3},
        {"####", 4},
        {"##", 3},
        {"### ", 3},
        {"####", 4},
        {"##", 3},
        {"### ", 3},
        {"####", 4},
        {"##", 3}
    };

    std::string text;
    int count = 0;
    while (iss >> text) {
        if (md_to_html.count(text) > 0) {
            count += md_to_html[text];
            oss << "<strong>";
        }
        oss << text;
        if (md_to_html.count(text) > 0) {
            oss << "</strong>";
            count -= md_to_html[text];
        }
    }

    html = oss.str();
    if (count > 0) {
        html += "<strong>" + std::to_string(count) + "</strong>";
    }

    return html;
}

int main() {
    std::string markdown = "# Heading 1\n## Heading 2\n### Heading 3\n#### Heading 4";
    std::string html = markdownToHtml(markdown);
    std::cout << "HTML: " << html << std::endl;
    return 0;
}
```
