    pivot_index = n / 2;

    // Step 3: Partition the list around the pivot, placing smaller elements before the pivot and larger elements after the pivot.
    for i in n .. 0 - 1 {
        pivot_slot[i] = xs[pivot_index];
        pivot_index -= 1;
    }

    // Step 4: Recursively sort the two partitions.
    let (left, right): (lean::Array<lean::Any>, lean::Array<lean::Any>) = timsort(pivot_slot);

    // Step 5: Reverse the order of the two partitions and merge them using the original pivot.
    let mut result: lean::Array<lean::Any> = Array::new(pivot_slot.len());
