std::string markdownToHtml(std::string& markdown) {
    // Regex and replace for headers of levels H1 to H6
    std::regex headerRegex(R"(^(#{1,6})\s*(.*))", std::regex_constants::multiline);
    std::string html = std::regex_replace(markdown, headerRegex, [](const std::smatch& m) {
        size_t level = m.str(1).size();
        return "<h" + std::to_string(level) + ">" + m.str(2).trim() + "</h" + std::to_string(level) + ">\n";
    });

