    html = std::regex_replace(html, boldRegex, "<strong>$1</strong>");

    // Regex and replace for italic text
    std::regex italicRegex(R"(\*(.*?)\*)");
    html = std::regex_replace(html, italicRegex, "<em>$1</em>");

    // Regex and replace for links
    std::regex linkRegex(R"(\[([^\]]+)\]\(([^)]+)\))");
    html = std::regex_replace(html, linkRegex, "<a href=\"$2\">$1</a>");

    // Convert standalone lines into paragraphs
    std::regex pRegex(R"(^\s*(\S[^\n]*\S)\s*$)", std::regex_constants::multiline);
    html = std::regex_replace(html, pRegex, "<p>$1</p>");

    return html;
