function isPrime(num) {
    if (num < 2) return false; // No numbers less than 2 are prime
    for (let i = 2; i * i <= num; i++) {
        if (num % i === 0) return false; // Check if num is divisible by any number up to its square root
    }
    return true; // If no divisors found, num is prime
}

// Function to check an array of five numbers and print primes
function printPrimeNumbers(numbers) {
    if (numbers.length !== 5) {
        console.error("Please provide exactly 5 numbers.");
        return;
    }

    numbers.forEach(number => {
        if (isPrime(number)) {
            console.log(number); // Print each prime number found in the array
        }
    });
}

// Example usage:
printPrimeNumbers([11, 3, 15, 20, 7]); // Expected output: 11, 3, 7
```

