public class PowerOfThree {
    public static boolean isPowerOfThree(int n) {
        // The largest power of three that fits within an int
        int maxPowerOfThree = 1162261467;  // 3^19

        // A number is a power of three if it divides maxPowerOfThree exactly without leaving a remainder
        return n > 0 && maxPowerOfThree % n == 0;
    }

    public static void main(String[] args) {
        // Testing the solution with example cases
        System.out.println(isPowerOfThree(27));  // Output: true
        System.out.println(isPowerOfThree(0));   // Output: false
