    def __init__(self):
        self.stack1 = []
        self.stack2 = []

    def is_empty(self):
        return not self.stack1 or not self.stack2

    def enqueue(self, element):
        if not self.stack1:
            self.stack1.append(element)
        else:
            self.stack2.append(element)

