std::string markdownToHtml(std::string& markdown) {
    std::vector<std::string> tokens;
    std::stringstream ss(markdown);
    std::string token;
    while (std::getline(ss, token, '#')) {
        tokens.push_back(token);
    }
    std::string html = "<ul>";
    for (int i = 0; i < tokens.size(); i++) {
        html += "<li>" + tokens[i] + "</li>";
    }
    html += "</ul>";
    return html;
}

int main() {
    std::string markdown = "# This is a markdown formatted text";
    std::string html = markdownToHtml(markdown);
    std::cout << html << std::endl;
    return 0;
}

