// Classes
struct snake_part {
    int x;
    int y;
};

struct snake {
    struct snake_part *head;
    struct snake_part *tail;
};

struct enemy {
    int x;
    int y;
};

// Main function
int main() {
    // Initialize the game
    init_snake();
    init_enemy();

    // Start the game loop
    while (game_over == 0) {
        // Draw the background
        draw_background();

        // Draw the snake
        draw_snake();

        // Move the snake
        move_snake();

        // Check for collision
        check_collision();

        // Check for win or lose
        check_win();

        // Check for quit
        if (kbhit()) {
            if (getch() == 27) {
                game_over = 1;
            }
        }

        // Update the frame rate
        delay(FRAME_RATE);
    }

    // End the game
    game_over = 1;
    close();
    return 0;
}

// Functions
void init_snake() {
    // Create a new snake
    snake snake = {
        head = new struct snake_part {
            x = snake_x,
            y = snake_y
        };
        tail = snake;
    };

    // Set the snake color
    snake->head->color = snake_color;

    // Add the snake to the list
    snake_list.push_back(snake);
}

void init_enemy() {
    // Create a new enemy
    enemy enemy = {
        x = enemy_x,
        y = enemy_y
    };

    // Set the enemy color
    enemy->x = enemy_x;
    enemy->y = enemy_y;
    enemy->color = ENEMY_COLOR;

    // Add the enemy to the list
    enemy_list.push_back(enemy);
}

void draw_background() {
    // Fill the screen with black
    fill(0);
}

void draw_snake() {
    // Draw the snake
    for (int i = 0; i < snake_list.size(); i++) {
        snake_part *part = snake_list[i];
        draw_part(part);
    }
}

void draw_part(struct snake_part *part) {
    // Draw the part
    fill(part->color);
    stroke(0);
    rectangle(part->x, part->y, 10, 10);
}

void move_snake() {
    // Move the snake
    if (snake_list.size() > 1) {
        // Move the tail
        snake_list[snake_list.size() - 1].x = snake_list[snake_list.size() - 2].x;
        snake_list[snake_list.size() - 1].y = snake_list[snake_list.size() - 2].y;

        // Move the head
        snake_list[snake_list.size() - 1].x += 10;
        snake_list[snake_list.size() - 1].y += 10;
    }
}

void check_collision() {
    // Check for collision with the wall
    if (snake_list[snake_list.size() - 1].x < 0 || snake_list[snake_list.size() - 1].x > WIDTH - 10 || snake_list[snake_list.size() - 1].y < 0 || snake_list[snake_list.size() - 1].y > HEIGHT - 10) {
        game_over = 1;
    }

    // Check for collision with the snake body
    for (int i = 0; i < snake_list.size() - 1; i++) {
        if (snake_list[snake_list.size() - 1].x == snake_list[i].x && snake_list[snake_list.size() - 1].y == snake_list[i].y) {
            game_over = 1;
        }
    }

    // Check for collision with the enemy
    for (int i = 0; i < enemy_list.size(); i++) {
        if (snake_list[snake_list.size() - 1].x == enemy_list[i].x && snake_list[snake_list.size() - 1].y == enemy_list[i].y) {
            game_over = 1;
        }
    }
}

void check_win() {
    // Check for win
    if (snake_list.size() == snake_length) {
        score++;
        snake_list.clear();
        snake_list.push_back(snake);
    }
}

void check_lose() {
    // Check for lose
    if (game_over == 1) {
        lives--;
        if (lives == 0) {
            game_over = 1;
        }
    }
}

