def longest_common_subsequence(s1, s2):
    # create a matrix of size (len(s1) + 1) x (len(s2) + 1)
    matrix = [[0 for j in range(len(s2) + 1)] for i in range(len(s1) + 1)]
    # fill the matrix with the lengths of common subsequences
    for i in range(1, len(s1) + 1):
        for j in range(1, len(s2) + 1):
            if s1[i - 1] == s2[j - 1]:
                matrix[i][j] = matrix[i - 1][j - 1] + 1
            else:
                matrix[i][j] = max(matrix[i - 1][j], matrix[i][j - 1])
    # backtrack from the bottom right corner of the matrix to find the subsequence
    subsequence = ""
    i = len(s1)
    j = len(s2)
    while i > 0 and j > 0:
        if s1[i - 1] == s2[j - 1]:
            subsequence = s1[i - 1] + subsequence
            i -= 1
            j -= 1
        elif matrix[i - 1][j] > matrix[i][j - 1]:
            i -= 1
        else:
            j -= 1
    return subsequence

