```cpp
#include <iostream>
#include <fstream>
#include <string>
#include <vector>
#include <algorithm>

int main() {
    std::ifstream file("file.txt"); // Replace "file.txt" with the name of your text file
    std::string word;
    std::vector<std::string> lines;

    if (file.is_open()) {
        while (file >> word) {
            lines.push_back(word);
        }
        file.close();
    }

    // Count the occurrences of the word
    int wordCount = 0;
    for (const std::string& line : lines) {
        if (line.find(word) != std::string::npos) {
            wordCount++;
        }
    }

    std::cout << "Word:" << word << " Occurrences: " << wordCount << std::endl;

    return 0;
}
```

