#include <string>
#include <cstdlib> // For system

std::string markdownToHtml(const std::string& markdown) {
    // Define the command to run pandoc
    std::string cmd = "pandoc -f markdown -t html";

    // Use system to execute the command
    const char* command = cmd.c_str();
    std::system(command);

    // Read the output. This is a simplified example and might need adjustments.
    FILE *pipe = popen(command, "r");
    if (!pipe) {
        std::cerr << "Failed to open pipe" << std::endl;
        return "";
    }

    char buffer[128];
    std::string result = "";

    while (fgets(buffer, sizeof(buffer), pipe) != nullptr) {
        result += buffer;
    }

    pclose(pipe);

    return result;
}

int main() {
    std::string markdown = "Hello, **world**!\n";
    std::string html = markdownToHtml(markdown);
    std::cout << "HTML Output:\n" << html << std::endl;

    return 0;
}
